НоваяЗапись = Приемник.ДополнительныеРеквизиты.Добавить();
НоваяЗапись.Реквизит = "Организация";
НоваяЗапись.Значение = ВернутьЗначениеДопРеквизитаИзДокумента("Грузоперевозчик", Приемник.ДополнительныеРеквизиты);

НоваяЗапись = Приемник.ДополнительныеРеквизиты.Добавить();
НоваяЗапись.Реквизит = "Заказчик";
НоваяЗапись.Значение = ВернутьЗначениеДопРеквизитаИзДокумента("Грузоперевозчик", Приемник.ДополнительныеРеквизиты);
//-

Если ЗначениеЗаполнено(СтруктураДокумента) Тогда
	ТЗШтрафы = Новый ТаблицаЗначений;
	ТЗАкты = Новый ТаблицаЗначений;
	ТЗСвАктВзв = Новый ТаблицаЗначений;
    ТЗГруз = Новый ТаблицаЗначений; 
    СтруктураДанных = Новый Структура;

	ДобавитьКолонкиВТаблицуТЧ(ТЗГруз, ТЗШтрафы, ТЗАкты, ТЗСвАктВзв, Приемник.ВидДокумента);

    ТЗГруз.Колонки.Добавить("Номенклатура");
	ТЗГруз.Колонки.Добавить("Маркировка");	
	//Проверяем наличие ТЧ
	Если СтруктураДокумента.Свойство("Документ_СодИнфГП_ПриемГрузГП_СвПринПоНаим_") Тогда 

		МассивДанных = СтруктураДокумента.Документ_СодИнфГП_ПриемГрузГП_СвПринПоНаим_;					

		//Пишем данные в таблицу значений
		Для Каждого СтруктураТЧ Из МассивДанных Цикл 
			Для Каждого ЭлСтруктураТЧ Из СтруктураТЧ Цикл 
				Для Каждого МасСтруктур Из ЭлСтруктураТЧ.Значение Цикл
					НоваяСтрока = ТЗГруз.Добавить(); 
					ЗаполнитьЗначенияСвойств(НоваяСтрока, МасСтруктур);	
				КонецЦикла;
			КонецЦикла;		
		КонецЦикла; 

		//Переименовываем колонки, чтобы их видела форма
		Для Каждого Колонка Из ТЗГруз.Колонки Цикл
			МассивЗначений = DR_ОбщегоНазначения.DR_СтрРазделить(Колонка.Имя,"_");						
			Колонка.Имя = МассивЗначений[МассивЗначений.Количество()-1];

			Если Колонка.Имя = "КолМест" Тогда
				Колонка.Имя = "КолМестГр";
			КонецЕсли;	
		КонецЦикла;                                                              

        ЗаписатьТаблицуЗначенийВСтруктуру(ТЗГруз, "СведенияОГрузе", СтруктураДанных);   

	КонецЕсли;

	ТЗШтрафы.Колонки.Добавить("ОтмАктВзв");	      
	Если СтруктураДокумента.Свойство("Документ_СодИнфГП_ОтметГП_РазмШтр_") Тогда 

		МассивДанных = СтруктураДокумента.Документ_СодИнфГП_ОтметГП_РазмШтр_;

		Для Каждого СтруктураТЧ Из МассивДанных Цикл
			НоваяСтрока = ТЗШтрафы.Добавить(); 
			ЗаполнитьЗначенияСвойств(НоваяСтрока, СтруктураТЧ);			
		КонецЦикла; 

		Для Каждого Колонка Из ТЗШтрафы.Колонки Цикл
			МассивЗначений = DR_ОбщегоНазначения.DR_СтрРазделить(Колонка.Имя,"_");						
			Колонка.Имя = МассивЗначений[МассивЗначений.Количество()-1]; 	
		КонецЦикла;

        ЗаписатьТаблицуЗначенийВСтруктуру(ТЗШтрафы, "СведенияШтрафов", СтруктураДанных);   				
	КонецЕсли; 

    ТЗАкты.Колонки.Добавить("ОтмАктВзв");
    Если СтруктураДокумента.Свойство("Документ_СодИнфГП_ОтметГП_СвАкт_") Тогда 

		МассивДанных = СтруктураДокумента.Документ_СодИнфГП_ОтметГП_СвАкт_;							

		Для Каждого СтруктураТЧ Из МассивДанных Цикл
			НоваяСтрока = ТЗАкты.Добавить(); 
			ЗаполнитьЗначенияСвойств(НоваяСтрока, СтруктураТЧ);			
		КонецЦикла; 

		Для Каждого Колонка Из ТЗАкты.Колонки Цикл
			МассивЗначений = DR_ОбщегоНазначения.DR_СтрРазделить(Колонка.Имя,"_");						
			Колонка.Имя = МассивЗначений[МассивЗначений.Количество()-1]; 	
		КонецЦикла;

        ЗаписатьТаблицуЗначенийВСтруктуру(ТЗАкты, "СведенияИныхАктов", СтруктураДанных);   	

	КонецЕсли;

	ТЗСвАктВзв.Колонки.Добавить("ОтмАктВзв");	
	Если СтруктураДокумента.Свойство("Документ_СодИнфГП_ОтметГП_СвАктВзв_") Тогда 

		МассивДанных = СтруктураДокумента.Документ_СодИнфГП_ОтметГП_СвАктВзв_;					

		Для Каждого СтруктураТЧ Из МассивДанных Цикл
			НоваяСтрока = ТЗСвАктВзв.Добавить(); 
			ЗаполнитьЗначенияСвойств(НоваяСтрока, СтруктураТЧ);			
		КонецЦикла; 

		Для Каждого Колонка Из ТЗСвАктВзв.Колонки Цикл
			МассивЗначений = DR_ОбщегоНазначения.DR_СтрРазделить(Колонка.Имя,"_");						
			Колонка.Имя = МассивЗначений[МассивЗначений.Количество()-1]; 	
		КонецЦикла;

        ЗаписатьТаблицуЗначенийВСтруктуру(ТЗСвАктВзв, "СведенияАктовВзвешивания", СтруктураДанных);   	

	КонецЕсли;

	//ДобавитьЗначениеХЗВРеквизитОбъекта(Приемник, СтруктураДанных, "ДанныеДокумента", МассивЛогаСобытий);  
	НоваяЗапись = Приемник.ДополнительныеРеквизиты.Добавить();

	НоваяЗапись.Реквизит = "Таблицы";
	НоваяЗапись.ХранилищеЗначения = Новый ХранилищеЗначения(СтруктураДанных); 

КонецЕсли; 

Приемник.Статус = ПолучитьЗначение_Перечисления("Статусы","Обработан");
