УИД_Основания = ВернутьЗначениеДопРеквизитаИзДокумента("Документ_ИдПУДИнфПрв_ИдФайлПУДИнфПрв", Приемник.ДополнительныеРеквизиты);

ЗапросТ5 = Новый Запрос;

ЗапросТ5.Текст =
"ВЫБРАТЬ
|	DR_Документы.Ссылка КАК Ссылка
|ИЗ
|	Документ.DR_Документы КАК DR_Документы
|ГДЕ
|	DR_Документы.ВидДокумента = ""ON_TRNPUDPER_Исходящий""
|	И DR_Документы.ИдентификаторДокумента = &ИдентификаторДокумента"; 

ЗапросТ5.УстановитьПараметр("ИдентификаторДокумента", УИД_Основания);

РезультатЗапроса = ЗапросТ5.Выполнить(); 
Результат = РезультатЗапроса.Выгрузить();

Для Каждого Строка Из Результат Цикл
	ДопРеквизитыДок = Строка.Ссылка.ДополнительныеРеквизиты;

	СписокРеквизитов.Вставить("Грузополучатель", ВернутьЗначениеДопРеквизитаИзДокумента("Грузополучатель", ДопРеквизитыДок));                
КонецЦикла;                        

Если ЗначениеЗаполнено(ВернутьЗначениеДопРеквизитаИзДокумента("НаимДокОснованиеФХЖ", Приемник.ДополнительныеРеквизиты)) Или ЗначениеЗаполнено(ВернутьЗначениеДопРеквизитаИзДокумента("ИдДокОснованиеФХЖ", Приемник.ДополнительныеРеквизиты)) Тогда  
	СписокРеквизитов.Вставить("ЕстьОснованиеФХЖ", Истина);
КонецЕсли;   

Если ЗначениеЗаполнено(ВернутьЗначениеДопРеквизитаИзДокумента("ИННЮЛ", Приемник.ДополнительныеРеквизиты)) Тогда
	ТипСтороны = "ЮЛ";
ИначеЕсли ЗначениеЗаполнено(ВернутьЗначениеДопРеквизитаИзДокумента("ИННФЛ", Приемник.ДополнительныеРеквизиты)) Тогда
	ТипСтороны = "ФЛ";
ИначеЕсли ЗначениеЗаполнено(ВернутьЗначениеДопРеквизитаИзДокумента("ИдСтатИЛ", Приемник.ДополнительныеРеквизиты)) Тогда
 	ТипСтороны = "ИЛ";

	ИдСтатИЛ = ВернутьЗначениеДопРеквизитаИзДокумента("ИдСтатИЛ", Приемник.ДополнительныеРеквизиты);

	Если ИдСтатИЛ = "ЮЛ" Тогда
		СписокРеквизитов.Вставить("НаимИЛЮЛ", ВернутьЗначениеДопРеквизитаИзДокумента("НаимИЛФЛ", Приемник.ДополнительныеРеквизиты));
	КонецЕсли;
КонецЕсли; 
СписокРеквизитов.Вставить("ТипСтороны", ТипСтороны);

Если ЗначениеЗаполнено(ВернутьЗначениеДопРеквизитаИзДокумента("НаимДокОснованиеОплата", Приемник.ДополнительныеРеквизиты)) Или ЗначениеЗаполнено(ВернутьЗначениеДопРеквизитаИзДокумента("ИдДокОснованиеОплата", Приемник.ДополнительныеРеквизиты)) Тогда
	СписокРеквизитов.Вставить("ЕстьОснованиеОплата", Истина);
КонецЕсли;  

Если ЗначениеЗаполнено(ВернутьЗначениеДопРеквизитаИзДокумента("ИННЮЛОплата", Приемник.ДополнительныеРеквизиты)) Тогда
	ТипСтороныОплата = "ЮЛ";
ИначеЕсли ЗначениеЗаполнено(ВернутьЗначениеДопРеквизитаИзДокумента("ИННФЛОплата", Приемник.ДополнительныеРеквизиты)) Тогда
	ТипСтороныОплата = "ФЛ";
ИначеЕсли ЗначениеЗаполнено(ВернутьЗначениеДопРеквизитаИзДокумента("ИдСтатИЛОплата", Приемник.ДополнительныеРеквизиты)) Тогда
 	ТипСтороныОплата = "ИЛ";

	ИдСтатИЛОплата = ВернутьЗначениеДопРеквизитаИзДокумента("ИдСтатИЛОплата", Приемник.ДополнительныеРеквизиты);

	Если ИдСтатИЛОплата = "ЮЛ" Тогда
		СписокРеквизитов.Вставить("НаимИЛЮЛОплата", ВернутьЗначениеДопРеквизитаИзДокумента("НаимИЛФЛОплата", Приемник.ДополнительныеРеквизиты));
	КонецЕсли;
КонецЕсли; 
СписокРеквизитов.Вставить("ТипСтороныОплата", ТипСтороныОплата);

Если ЗначениеЗаполнено(ВернутьЗначениеДопРеквизитаИзДокумента("НомерСЧ", Приемник.ДополнительныеРеквизиты)) Тогда
	СписокРеквизитов.Вставить("ЕстьБанковскиеРеквизиты", Истина);
КонецЕсли;                         


Для Каждого Запись из СписокРеквизитов Цикл

	Отбор = Новый Структура;
	Отбор.Вставить("Реквизит", Запись.Ключ);

	СтрокиПоиск = Приемник.ДополнительныеРеквизиты.НайтиСтроки(Отбор);
	Если СтрокиПоиск.Количество() Тогда
		НоваяЗапись = СтрокиПоиск[0];	
	Иначе
		НоваяЗапись = Приемник.ДополнительныеРеквизиты.Добавить();
	КонецЕсли;

	НоваяЗапись.Реквизит = Запись.Ключ;
	НоваяЗапись.Значение = Запись.Значение;

КонецЦикла;                        


Приемник.Статус = ПолучитьЗначение_Перечисления("Статусы","Обработан");
