	 Строка.НалКоорТочВрУб 	 	= "0";
	 Строка.ЗамДатВрПосПриемСтр = Строка(Формат(Строка.ЗамДатВрПосПрием,"ДФ=dd.MM.yyyy")) + "T" + Формат(Строка.ЗамДатВрПосПрием,"ДФ=чч:мм:сс") + ПустойUTC;
	 Строка.НалКоорТочВрПрм	 	= "0";

	 //Сведения о Подписанте
	 Попытка
		 ДанныеПоОрганизации = DR_GLN_Прочитать(Строка.Организация, "Грузоперевозчик");
		 Сертификат = ДанныеПоОрганизации.Сертификат;
		 Строка.СтатПодп			= Сертификат.СтатусПодписанта;
