	Строка.ДатВрПАСтр 		= Строка(Формат(ТекущаяДата(),"ДФ=dd.MM.yyyy")) + "T" + Формат(ТекущаяДата(),"ДФ=чч:мм:сс") + ПустойUTC;
	Строка.ДатаИспрСтр     	= Строка(Формат(Строка.ДатаИспр,"ДФ=dd.MM.yyyy"));
