	Строка.ДатВрНовВыгрПАСтр = Строка(Формат(Строка.ДатВрНовВыгрПА,"ДФ=dd.MM.yyyy")) + "T" + Формат(Строка.ДатВрНовВыгрПА,"ДФ=чч:мм:сс") + ПустойUTC;
