	//ОтметПА
	//Строка.ДатВрОтметСтр 	= Строка(Формат(Строка.ДатВрОтмет,"ДФ=dd.MM.yyyy")) + "T" + Формат(Строка.ДатВрОтмет,"ДФ=чч:мм:сс") + ПустойUTC;
